import './style.scss'

import React, { useState, useEffect, useRef, createRef } from 'react';
import mockData from './mockData';
import Fuse from "fuse.js";
import Card from './Card';

function AddRecipe(props) {
  const [addModalOpen, setOpen] = useState(false)
  const [data, setData] = useState([])
  // const addRef = useRef(null);
  const [searchQuery, setQuery] = useState('')

  useEffect(() => {
    fetch('/recipe')
    .then(response => response.json())
    .then(data => console.log(data));
    setData(mockData)
  }, []);

  const handleChange = e => {
    const query = e.target.value
    console.log(query, 'jhsdjkd')
    setQuery(query)
    const fuse = new Fuse(mockData, {keys: [
      'name',
      'ingredients'
    ]});
    const result = fuse.search(query).map((res) => res.item);
    setData(result)
    if(query === '') {
      setData(mockData)
    }
  };

  // const addSmoothie = e => {
  //   addRef.current.style.display = "block";
  //   setOpen(true)
  // };
  const search = (e)=> {
    e.preventDefault();
  }

  return (
    <>
      <form>
        <input onChange={handleChange} className='search' type="text" placeholder="Search.." name="search" id="query" value={searchQuery}/>
        {/* <button onClick={search}><i class="fa fa-search"></i></button> */}
      </form>
      {/* <button onClick={addSmoothie} className='recipe-button'>Add</button> */}
      {data.map(entry => {
        return <Card key={entry.name} data={entry}/>
      })}
    </>)
}

export default AddRecipe;
