import './card.scss'
import { RiCloseCircleLine } from 'react-icons/ri';
import { TiEdit } from 'react-icons/ti';
import { useRef, useState } from 'react';
import AddRecipeModal from '../AddRecipeModal/AddRecipeModal';
import ConfirmationModal from '../ConfirmationModal/ConfirmationModal';

function Card(props) {
  const {data, editRecipe} = props

  const recipeRef = useRef(null);
  const confirmationRef = useRef(null);

  const removeRecipe = (name) => {
    // e.preventDefault();
    confirmationRef.current.style.display = "block";
  };

  // const editRecipe = (e, name) => {
  //   e.preventDefault();
  //   setIsEdit(true)
  //   for(let data of initialData) {
  //     if(data.name === name) {
  //       setDataToUpdate(data)
  //       // break
  //     }
  //   }
  //   recipeRef.current.style.display = "block";
  // };

  return data.map((entry, ind)=> {
    const ingredients = entry.ingredients.split(',')
    return <div className='cardIcons'><div className='card' key={data.name}>
    <div className="container">
      <h2><b>Title:</b></h2> 
      <h4>{entry.title}</h4> 
      <h2><b>Name:</b></h2> 
      <h4>{entry.name}</h4> 
      <h2><b>Ingredients:</b></h2> 
      {ingredients.map(ing => <h4 onClick={()=> {}}>{ing}</h4> 
      )}
    </div>
    </div>
    <div className='icons'>
        <RiCloseCircleLine
          onClick={() => removeRecipe(entry.name)}
          className='delete-icon'
        />
        {/* <TiEdit
          onClick={e => editRecipe(e, entry.name)}
          className='edit-icon'
        /> */}
    </div>
        <ConfirmationModal data={entry} ref={confirmationRef} key={entry.name} />
        {/* <AddRecipeModal ref={recipeRef}/> */}
  </div>
})}

export default Card;
