    addRef.current.style.display = "block";
    setOpen(true)
  };

    return <div className="header">
    <div className="Title">
      <h1>My Smoothies</h1>
      <button onClick={addSmoothie} className='recipe-button'>Add</button>
    </div>
  </div>
}

export default Banner;
