import React, { useState, useEffect, useRef, createRef } from 'react';
import './AddRecipeModal.scss'
const AddRecipeModal = React.forwardRef(function AddRecipeModal(props, addRef) {

    const [amount, setAmount] = useState()
    const [ingrName, setIngrName] = useState('')
    const [unit, setUnit] = useState('')
    const [ingredients, setIngredients] = useState([])
    const onModalCancel = e => {
        addRef.current.style.display = "none";
    }

    const handleChangeInput = e => {
        setAmount(e.target.value)
    }
    const handleChangeName = e => {
        setIngrName(e.target.value)
    }

    const handleUnitChange = e => {
        setUnit(e.target.value)
    }

    const handleNewIngredient = (e) => {
        e.preventDefault();
        setAmount();
        setUnit(undefined);
        setIngrName("");
        const newIngredient =
            {"amount" : amount,
             "unit" : unit,
             "ingredient": ingrName
            };
        setIngredients([...ingredients, newIngredient])
        setIngrName('')
        setAmount(null)
        setUnit(undefined);
    };

    const  allIngredients = ()=>{
        return ingredients.map(ingredient =>{
            return(
                <p className="ingredient-text">{ingredient.amount} {ingredient.unit} {ingredient.ingredient} </p> 
            )
        })
    };
    return (
        <div id="newRecipe" ref={addRef} className="modal">
            <div className="modal-content">
            <span onClick={onModalCancel} className="close">&times;</span>
            <h4>Add New Recipe</h4>
            <form className="ingredient-form" onSubmit={handleNewIngredient} id="ingredient-form" >
                    <table>  
                        <label className="ingredients" htmlFor="ingredients">Ingredients:</label>
                        <tr>
                        <td><label htmlFor="amount">Amount:</label></td>
                            <input onChange={handleChangeInput} type="number" step="any" id="amount" value={amount} required placeholder="Enter amount"/>
                        </tr>
                        <tr>
                            <td><label htmlFor="unit">Unit:</label></td>
                            <input onChange={handleChangeInput} type="number" step="any" id="amount" value={amount} required placeholder="Enter unit"/>
                            {/* <select className="unitSelect" onChange={handleUnitChange} type="text" id="unit" value={unit}>
                                <option value={undefined}></option>
                                <option value="cup">cup</option>
                                <option value="g">g</option>
                                <option value="ml">ml</option>
                                <option value="tsp">tsp</option>
                            </select> */}
                        </tr>
                        <tr>
                            <td><label htmlFor="ingredient">Ingredient:</label></td>
                            <input className="input-add-recipe-ingredient" onChange={handleChangeName} type="text" id="ingredient" value={ingrName}/>
                            <a className="plus" colSpan="2"><input type="submit" value="+" id="save" border="0"/></a>
                            </tr>
                    </table>      
                        {allIngredients()}
                </form>
            {/* <div className="input-field">
                <input id="add_name" type="text" className="validate"/>
                <label htmlFor="add_name">Name</label>
                </div>
                <div className="input-field">
                <input id="add_ingredients" type="text" className="validate"/>
                <label htmlFor="add_ingredients">Ingredients</label>
            </div>
            <div className="input-field">
                <input id="add_photo" type="text" className="validate"/>
                <label htmlFor="add_photo">Photo</label>
            </div> */}
            </div>
            {/* <div className="modal-footer">
            <a onClick={() => {}} href="#!" className=" modal-action modal-close waves-effect waves-green btn-flat">Agree</a>
            </div> */}
        </div>
    )
});

export default AddRecipeModal;
