import React, { useState, useEffect, useRef, createRef } from 'react';
import './AddRecipeModal.scss'
const AddRecipeModal = React.forwardRef(function AddRecipeModal(props, addRef) {

    const [amount, setAmount] = useState()
    const [ingrName, setIngrName] = useState('')
    const [unit, setUnit] = useState('')
    const [ingredients, setIngredients] = useState([])
    const onModalCancel = e => {
        addRef.current.style.display = 'none';
    }

    const handleChangeInput = e => {
        setAmount(e.target.value)
    }
    const handleChangeName = e => {
        setIngrName(e.target.value)
    }

    const handleUnitChange = e => {
        setUnit(e.target.value)
    }

    const handleNewIngredient = (e) => {
        e.preventDefault();
        setAmount();
        setUnit(undefined);
        setIngrName('');
        const newIngredient =
            {'amount' : amount,
             'unit' : unit,
             'ingredient': ingrName
            };
        setIngredients([...ingredients, newIngredient])
        setIngrName('')
        setAmount('')
        setUnit('');
    };

    const  allIngredients = ()=>{
        return ingredients.map(ingredient =>{
            return(
                <p className='ingredient-text'>{ingredient.amount} {ingredient.unit} {ingredient.ingredient} </p> 
            )
        })
    };
    return (
        <div id='newRecipe' ref={addRef} className='modal'>
            <div className='modal-content'>
            <span onClick={onModalCancel} className='close'>&times;</span>
            <h4>Add New Recipe</h4>
            <form onSubmit={handleNewIngredient} id='ingredient-form' >
                <div className='container'>
                    <div className='amount'>
                        <label htmlFor='amount'>Amount:</label>
                        <input className='input-holder' onChange={handleChangeInput} type='text' step='any' value={amount} required placeholder='Enter amount'/>
                    </div>
                    <div className='unit'>
                        <label htmlFor='unit'>Unit:</label>
                        <input className='input-holder' onChange={handleUnitChange} type='text' step='any' value={unit} required placeholder='Enter unit for ingredient'/>
                    </div>
                    <div className='block'>
                        <label htmlFor='ingredient' className='label-holder'>Ingredient:</label>
                        <input className='input-holder' onChange={handleChangeName} type='text' value={ingrName} required placeholder='Enter name for ingredient'/>
                        <button onClick={() => {}} className='save-button' type='submit'>
                            +
                        </button>
                    </div>
                </div>
                {allIngredients()}
            </form>
            </div>
        </div>
    )
});

export default AddRecipeModal;
