import '../style.scss'

import React, { useState, useEffect, useRef, createRef } from 'react';
import Fuse from "fuse.js";
import Card from '../Card/Card'

function AddRecipe(props) {
  const [data, setData] = useState([])
  const [searchQuery, setQuery] = useState('')
  const [initialData, setInitialData] = useState([])

  useEffect(() => {
    fetch('/recipes')
      .then(response => response.json())
      .then(data => {
        setData(data.recipes)
        setInitialData(data.recipes)
      });
  }, []);

  const handleChange = e => {
    const query = e.target.value
    setQuery(query)
    const fuse = new Fuse(data, {keys: [
      'name',
      'ingredients'
    ]});
    const result = fuse.search(query).map((res) => res.item);
    setData(result)
    if(query === '') {
      setData(initialData)
    }
  };

  const search = (e)=> {
    e.preventDefault();
  }
  // console.log(data, 'datataa')
  return (
    <>
      <form>
        <input onChange={handleChange} className='search' type="text" placeholder="Search.." name="search" id="query" value={searchQuery}/>
      </form>
      {data.map(entry => {
        return <Card key={entry.name} data={entry}/>
      })}
          <ConfirmationModal ref={confirmationRef} data={data} />
    <AddRecipeModal ref={recipeRef} />
    </>)
}

export default AddRecipe;
