import React, { useEffect, useState } from 'react';
import './AddRecipeModal.scss'
const AddRecipeModal = React.forwardRef(function AddRecipeModal(props, addRef) {

    const [amount, setAmount] = useState('')
    const [ingrName, setIngrName] = useState('')
    const [recName, setRecName] = useState('')
    // const [unit, setUnit] = useState('')
    const [ingredients, setIngredients] = useState([])
    const onModalCancel = e => {
        addRef.current.style.display = 'none';
    }

    const handleChangeName = e => {
        const targetVal = e.target.value
        const ingArr = targetVal.split(',')
        setIngrName(e.target.value)
        setIngredients([...ingArr])
    }

    const handleChange = e => {
        setRecName(e.target.value)
    }

    const handleNewIngredient = (e) => {
        e.preventDefault();
        setAmount();
        setIngrName('');
        const newIngredient =
            {'amount' : amount,
             'ingredient': ingrName
            };
        setIngredients([...ingredients, newIngredient])
        setIngrName('')
        setAmount('')
    };

    const addIngredientsToStore = async () => {
        const response = await fetch('/recipes', {name: recName, ingredients: ingrName, id: 1},function(data){
            if(data === 'yes') {
                alert("login success");
              }
          });
        setIngrName('')
        setRecName('')
    }

    const  showIngredients = ()=>{
        return ingredients.map(ingredient =>{
            return(
                <p >{ingredient.amount} {ingredient.ingredient}</p> 
            )
        })
    };
    return (
        <div id='newRecipe' ref={addRef} className='modal'>
            <div className='modal-content'>
            <span onClick={onModalCancel} className='close'>&times;</span>
            <form onSubmit={handleNewIngredient} id='ingredient-form' >
                <div className='formContainer'>
                    <div className='content'>
                        <label htmlFor='name'>Name:</label>
                        <input onChange={handleChange} type='text' step='any' value={recName} required placeholder='Enter name of recipe'/>
                    </div>
                    <div className='content'>
                        <label htmlFor='ingredient'>Ingredient:</label>
                        <textarea rows="4" cols="50" onChange={handleChangeName} type='text' value={ingrName} required placeholder='Enter ingredients'/>
                    </div>
                </div>
                {showIngredients()}
            </form>
            <button onClick={addIngredientsToStore} className='save-button'>
                Add
            </button>
            </div>
        </div>
    )
});

export default AddRecipeModal;
