import { useEffect, useState } from "react";
import axios from 'axios'

function Login() {

    const [password, setPassword] = useState('')
    const [username, setUsername] = useState('')
    const [data, setdata] = useState('')
    // useEffect(() => {
    //     fetch('/users')
    //     .then(async response => response.json())
    //     .then(data => {
    //         console.log(data)
    //     });
    // }, [])
    const handleChangePass = (e) => {
        setPassword(e.target.value)
    }

    const handleChangeUserName = (e) => {
        setUsername(e.target.value)
    }

    const onLogin = () => {
      axios.post('/users', {
        username: 'Siddhi',
        password: 'abcd123'
      })
      .then((response) => {
        console.log(response, 'hdgdgd')
        if(response.data.uuid) {
          window.localStorage.setItem('sessionId', response.uuid)
        }
      }, (error) => {
        console.log(error);
      });
    }
    const renderForm = (
        <div className="form">
          {/* <form> */}
            <div className="input-container">
              <label>Username </label>
              <input type="text" onChange={handleChangeUserName} value={username} required />
              {/* {renderErrorMessage("uname")} */}
            </div>
            <div className="input-container">
              <label>Password </label>
              <input type="password" onChange={handleChangePass} value={password} required />
              {/* {renderErrorMessage("pass")} */}
            </div>
            <button onClick={onLogin} className='save-button'>
                Login
            </button>
          {/* </form> */}
        </div>
     );

     return renderForm
}

export default Login;
