import './style.scss'

import React, { useState, useEffect, useRef, createRef } from 'react';
import AddRecipeModal from './AddRecipeModal';
import mockData from './mockData';
import Fuse from "fuse.js";
import Card from './Card';

function AddRecipe(props) {
  const [addModalOpen, setOpen] = useState(false)
  const [data, setData] = useState([])
  const addRef = useRef(null);
  const [query, setQuery] = useState('')

  // const [input, setInput] = useState(props.edit ? props.edit.value : '');

  // const inputRef = useRef(null);

  // useEffect(() => {
  //   inputRef.current.focus();
  // });

  const handleChange = e => {
    setQuery(e.target.value)
    const fuse = new Fuse(mockData, {keys: [
      'name',
      'ingredients'
    ]});
    const result = fuse.search(query).map((res) => res.item
    );
    setData(result)
  };

  const addSmoothie = e => {
    addRef.current.style.display = "block";
    setOpen(true)
  };

  const search = (e)=> {
    e.preventDefault();
    // const r = fuse.search(query).map((result) => {
    //   return result.item
    // });
  }
  console.log(data, '********')
  return (<>
          <form action="">
            <input onChange={handleChange} type="text" placeholder="Search.." name="search" id="query" value={query}/>
            <button onClick={search}><i class="fa fa-search"></i></button>
          </form>
          <button onClick={addSmoothie} className='recipe-button'>
            Add
          </button>
          {data.map(entry => {
            return <Card />
          })

          }
          <AddRecipeModal ref={addRef} />
        </>)
}

export default AddRecipe;
