    //   }).then(response => response.json())
    //     .then(data => {
    //       console.log(data, 'ffffff')
    //       if(data.status == 403) {
    //         window.location.href = '/'
    //       } else {
    //         window.location.href = '/home'
    //       }
    //     });
    // }, [])

    const handleChangePass = (e) => {
        setPassword(e.target.value)
    }

    const handleChangeUserName = (e) => {
        setUsername(e.target.value)
    }

    const onLogin = () => {
      if (isLoggedIn) return;

      fetch('/users', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({username, password})
        }).then(res => res.json())
        .then(res =>  {
          if(res.uuid) {
            window.localStorage.setItem('session', res.uuid)
            if (location.state?.from) {
              navigate(location.state.from);
            }
            console.log('doneeeeee')
          }
        });

    }
    const renderForm = (
        <div className="form">
          {/* <form> */}
            <div className="input-container">
              <label>Username </label>
              <input type="text" onChange={handleChangeUserName} value={username} required />
              {/* {renderErrorMessage("uname")} */}
            </div>
            <div className="input-container">
              <label>Password </label>
              <input type="password" onChange={handleChangePass} value={password} required />
              {/* {renderErrorMessage("pass")} */}
            </div>
            <button onClick={onLogin} className='save-button'>
                Login
            </button>
          {/* </form> */}
        </div>
     );

     return renderForm
}

export default Login;
