import React, { useEffect, useState } from 'react';

const ConfirmationModal = React.forwardRef(function ConfirmationModal({data}, confirmationRef) {

    const onModalCancel = e => {
        recipeRef.current.style.display = 'none';
    }

    const handleRemove = () => {
        fetch('/deleterecipe', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({name: data.name})
            }).then(res => res.json())
            .then(res => console.log(res, 'jhdksnfdhbj'));
    }

    return (
        <div id='newRecipe' ref={confirmationRef} className='modal'>
            <div className='modal-content'>
                <span onClick={onModalCancel} className='close'>&times;</span>
                <p>Are you sure you want to continue?</p>
                <button onClick={handleRemove}>confirm</button>
            </div>
        </div>
    )
})

export default ConfirmationModal;
