import { useEffect, useState } from "react";
import axios from 'axios'

function Login() {

    const [password, setPassword] = useState('')
    const [username, setUsername] = useState('')
    const [data, setdata] = useState('')
    // useEffect(() => {
    //     fetch('/users')
    //     .then(async response => response.json())
    //     .then(data => {
    //         console.log(data)
    //     });
    // }, [])

    useEffect(()=> {
      const sessionId = window.localStorage.getItem('session')
      console.log(sessionId, 'sessionIdsessionId')
      fetch('/sessions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({uuid: sessionId})
      }).then(response => response.json())
        .then(data => {
          console.log(data, 'ffffff')
          if(data.status == 404) {
            window.location = '/'
          } else {
            window.location = '/home'
          }
        });
    }, [])

    const handleChangePass = (e) => {
        setPassword(e.target.value)
    }

    const handleChangeUserName = (e) => {
        setUsername(e.target.value)
    }

    const onLogin = () => {

      fetch('/users', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({username, password})
        }).then(res => res.json())
        .then(res =>  {
          if(res.uuid) {
            window.localStorage.setItem('session', res.uuid)
            console.log('doneeeeee')
          }
        });

    }
    const renderForm = (
        <div className="form">
          {/* <form> */}
            <div className="input-container">
              <label>Username </label>
              <input type="text" onChange={handleChangeUserName} value={username} required />
              {/* {renderErrorMessage("uname")} */}
            </div>
            <div className="input-container">
              <label>Password </label>
              <input type="password" onChange={handleChangePass} value={password} required />
              {/* {renderErrorMessage("pass")} */}
            </div>
            <button onClick={onLogin} className='save-button'>
                Login
            </button>
          {/* </form> */}
        </div>
     );

     return renderForm
}

export default Login;
