import { useState } from "react";

function Login() {

    const [password, setPassword] = useState('')

    const [username, setUsername] = useState('')

    const handleChangePass = (e) => {
        setPassword(e.target.value)
    }

    const handleChangeUserName = (e) => {
        setUsername(e.target.value)
    }

    const onLogin = (e) => {
        console.log('hrrrr')
        fetch('/recipes')
        .then(response => response.json())
        .then(data => {
          setData(data.recipes)
          setInitialData(data.recipes)
        });
        console.log(password, username , '***')

    }

    const renderForm = (
        <div className="form">
          <form>
            <div className="input-container">
              <label>Username </label>
              <input type="text" name="uname" onChange={handleChangeUserName} value={username} required />
              {/* {renderErrorMessage("uname")} */}
            </div>
            <div className="input-container">
              <label>Password </label>
              <input type="password" name="pass" onChange={handleChangePass} value={password} required />
              {/* {renderErrorMessage("pass")} */}
            </div>
            <div className="button-container">
              <input type="submit" onClick={onLogin}/>
            </div>
          </form>
        </div>
     );

     return renderForm
}

export default Login;
