import { useEffect, useState } from "react";

function Login() {

    const [password, setPassword] = useState('')
    const [username, setUsername] = useState('')
    const [data, setdata] = useState('')
    // useEffect(() => {
    //     fetch('/users')
    //     .then(async response => response.json())
    //     .then(data => {
    //         console.log(data)
    //     });
    // }, [])
    const handleChangePass = (e) => {
        setPassword(e.target.value)
    }

    const handleChangeUserName = (e) => {
        setUsername(e.target.value)
    }

    const onLogin = () => {
      fetch('/users', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({username, password})
        }).then(async res => {
          console.log("Here waiting for response: ", res)
          const re = await res.json()
          console.log(re, 'kjnlkjdjhdj')
        })
        // .then(res => {
        //   if(res.uuid) {
        //     window.localStorage.setItem('session', res.uuid)
        //   }
        // });

    }
    const renderForm = (
        <div className="form">
          <form>
            <div className="input-container">
              <label>Username </label>
              <input type="text" onChange={handleChangeUserName} value={username} required />
              {/* {renderErrorMessage("uname")} */}
            </div>
            <div className="input-container">
              <label>Password </label>
              <input type="password" onChange={handleChangePass} value={password} required />
              {/* {renderErrorMessage("pass")} */}
            </div>
            <button onClick={onLogin} className='save-button'>
                Login
            </button>
          </form>
        </div>
     );

     return renderForm
}

export default Login;
